        }
    };

    const [searchTerm, setSearchTerm]=useState("");
    const API_URL = "https://www.omdbapi.com/?apikey=5baa7886";

    const searchMovies=async(title)=>{
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = response.json();


    }
