    <>
     <h>Jio HotStar</h>
                <div className='Search'>
                    <input type='text' id='searchtext' placeholder='Search Movie Name'
                    value={searchTerm}
                    onChange={(e)=>{setSearchTerm(e.target.value)}}
                    onKeyDown={handleKeyDown}
