import React from 'react';
import '../css/Header.css';
import logo from '../search.svg';
// import { Link } from "react-router-dom";

const Header = (props) => {

    const handleKeyDown = (e) =>{
        if(e.keyCode === 13)
        {
            props.searchMovies(props.searchTerm);
        }
    };



  return (
    <>
     <h>Jio HotStar</h>
                <div className='Search'>
                    <input type='text' id='searchtext' placeholder='Search Movie Name'
                    value={props.searchTerm}
                    onChange={(e)=>{props.setSearchTerm(e.target.value)}}
                    onKeyDown={handleKeyDown}
                    ></input>
                    <Link to='/search'><img src={logo} className='Search-logo' alt='logo' onClick={()=>props.searchMovies(props.searchTerm)}/></Link>
                </div>    
    </>
  )
}

export default Header;
